// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/cryptography/ECDSA.sol";

contract BounceERC1155WithSign is ERC1155, Ownable {
    address public signer;

    constructor(string memory uri) ERC1155(uri) public {}

    function setSigner(address _signer) external onlyOwner {
        signer = _signer;
    }

    function setURI(string calldata uri) external onlyOwner {
        super._setURI(uri);
    }

    function mint(address account, uint256 id, uint256 amount, bytes calldata data) external onlyOwner {
        super._mint(account, id, amount, data);
    }

    function burn(address account, uint256 id, uint256 amount) external onlyOwner {
        super._burn(account, id, amount);
    }

    function mintUser(uint256 id, uint256 amount, bytes calldata data, bytes calldata sign) external {
        bytes32 hash = ECDSA.toEthSignedMessageHash(keccak256(abi.encodePacked(msg.sender, id, amount)));
        require(ECDSA.recover(hash, sign) == signer, "INVALID SIGNER");
        super._mint(msg.sender, id, amount, data);
    }
}
